import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isHovered, setIsHovered] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Podaci za slanje
    const loginData = { email, password };

    try {
      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(loginData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Greška pri prijavi.');
      }

      const result = await response.json();
      console.log('Prijava uspješna:', result);

      // Preusmjeri korisnika na dashboard nakon uspješne prijave
      navigate('/dashboard');
    } catch (error) {
      console.error('Greška:', error.message);
      alert(error.message); // Prikaz greške korisniku
    }
  };

  const buttonStyle = {
    padding: '10px 20px',
    fontSize: '16px',
    fontWeight: '600',
    borderRadius: 5,
    backgroundColor: isHovered ? '#668dc0' : '#0f1c30',
    color: isHovered ? '#0f1c30' : '#668dc0',
    border: 'none',
    cursor: 'pointer',
    transition: 'background-color 0.5s, color 0.5s',
    fontFamily: 'Poppins, sans-serif',
  };

  return (
    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        backgroundColor: '#668DC0',
      }}
    >
      <div
        style={{
          textAlign: 'center',
          width: '100%',
          maxWidth: '400px',
          padding: '20px',
          backgroundColor: '#f0f8ff',
          borderRadius: '10px',
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        }}
      >
        <h1 style={{ color: '#0f1c30', marginBottom: '20px', fontFamily: 'Poppins, sans-serif' }}>
          Student Login
        </h1>
        <form onSubmit={handleSubmit}>
          <div style={{ margin: '10px 0' }}>
            <label style={{ display: 'block', marginBottom: '5px', color: '#333', fontFamily: 'Poppins, sans-serif' }}>
              Email:
            </label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={{
                width: '75%',
                padding: '10px',
                fontSize: '16px',
                borderRadius: 5,
                border: '1px solid #ccc',
                backgroundColor: '#fff',
              }}
            />
          </div>
          <div style={{ margin: '10px 0' }}>
            <label
              style={{
                display: 'block',
                marginBottom: '5px',
                color: '#333',
                fontFamily: 'Poppins, sans-serif',
              }}
            >
              Password:
            </label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={{
                width: '75%',
                padding: '10px',
                fontSize: '16px',
                borderRadius: 5,
                border: '1px solid #ccc',
                backgroundColor: '#fff',
              }}
            />
          </div>
          <div style={{ margin: '20px 0' }}>
            <button
              type="submit"
              style={buttonStyle}
              onMouseEnter={() => setIsHovered(true)}
              onMouseLeave={() => setIsHovered(false)}
            >
              Prijava
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default LoginForm;
